import"./style.css";document.addEventListener("DOMContentLoaded",function(){const t="copy-content";let e=document.getElementsByClassName(t),o=document.createElement("div");o.id="copy-notification",document.body.appendChild(o);for(let t=0;t<e.length;t++){let n=e[t];n.title="Click to copy",n.style.cursor="pointer",n.addEventListener("click",async function(){try{await navigator.clipboard.writeText(n.innerHTML),o.innerHTML="Content Copied!",o.classList.add("show"),setTimeout(()=>{o.classList.remove("show")},3e3)}catch(t){console.error("Failed to copy text: ",t)}})}});